import './WeatherApp.css'
import React, { useState } from 'react';
import axios from 'axios';

function WeatherApp(){

    const [data, setData] = useState({});
    const [location, setLocation] = useState('');

    const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=237607b761e39f9aee1751110270e432`;

    const searchLocation = (event) => {
      if (event.key === 'Enter') {
      axios.get(url).then((response) => {
        setData(response.data)
        console.log(response.data)
      })
    }
  }

    return(
        <>
        <div className="search">
            <div className="searchtitle">Enter a city name below: </div>
            <input
            value={location}
            onChange={event => setLocation(event.target.value)}
            placeholder= "Enter Location"
            onKeyPress={searchLocation}
            type="text"/>
        </div>
          <div className="top">
            <div className="location">
              <p>{data.name}</p>
            </div>
            <div className="temp">
              {data.name ? <h1>{Math.round(data.main.temp - 273.15)} Â°C</h1> : null}
            </div>

          </div>
          </>

    )
}

export default WeatherApp;
